uint8_t port = 0x5aU;
uint8_t result_8;
uint16_t result_16;
uint16_t mode;

result_8 = ( ~port ) >> 4;                           /* not compliant */
result_8 = ( ( uint8_t )( ~port ) ) >> 4;                /* compliant */
result_16 = ( ( uint16_t )( ~( uint16_t )port ) ) >> 4;  /* compliant */
result_16 = ( ( port << 4 ) & mode ) >> 6;           /* not compliant */
result_16 = ( ( uint16_t )( ( uint16_t )port << 4 ) & mode ) >> 6;
                                                     /* compliant */

